import React, { Component } from 'react';

class listBookComponent extends Component {
    constructor(props){
        super(props);

        this.state = {
            book: []
        }
    }

    render() {
        return (
            <div>
                <h2 className='text-center'>Book List</h2>
                <div className='row'>
                    <table className='table'>
                        <thead>
                            <tr>
                                <th>Book ID</th>
                                <th>Title</th>
                                <th>Author</th>
                                <th>Description</th>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.book.map(
                                    book => 
                                    <tr key={book.id}>
                                        <td>{book.id}</td>
                                        <td>{book.title}</td>
                                        <td>{book.author}</td>
                                        <td>{book.description}</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>

            </div>
        );
    }
}

export default listBookComponent;
